---------------------------------
-- MSSQL - CDC Type 1
---------------------------------

-- Creation of destination table 

    DECLARE @destination_table_exist AS INTEGER;
    DECLARE @val VARCHAR(1000);
    DECLARE @query VARCHAR(1000);
    SET @destination_table_exist = CASE WHEN OBJECT_ID('[AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ]', 'U') IS NULL THEN 0 ELSE 1 END;        
	IF @destination_table_exist = 0 
    BEGIN
        SELECT TOP 0 * INTO [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] FROM [staging].[EXPORT_FACTURE_LIGNES_GAZ];        
        ALTER TABLE [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] ADD [nx_hash] varchar(256);
        ALTER TABLE [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] ADD [nx_creation_date] DATETIME;
        ALTER TABLE [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] ADD [nx_update_date] DATETIME;        
        DECLARE cur CURSOR FOR Select [name] as [ColumnName] FROM sys.columns WHERE is_identity=1 AND [object_id] = OBJECT_ID('[AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ]', 'U');
        OPEN cur
        FETCH NEXT FROM cur INTO @val
        BEGIN
	        SET @query = 'ALTER TABLE [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] DROP COLUMN ' + @val;
	        EXEC (@query);
	        SET @query = 'ALTER TABLE [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] ADD ' + @val + ' BIGINT';
	        EXEC (@query);
	        FETCH NEXT FROM cur INTO @val
        END
        CLOSE cur;  
        DEALLOCATE cur;  
        CREATE INDEX [indx_AG_to_OC_EXPORT_FACTURE_LIGNES_GAZ] ON [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] ([ID_FACTURE]);
    END;      

-- Data insert

    DELETE FROM [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] WHERE ID_FACTURE IN (SELECT DISTINCT ID_FACTURE FROM [staging].[EXPORT_FACTURE_LIGNES_GAZ]);

    INSERT INTO [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] ([UNITPRICE],[UNITPRICECURRENCY],[USERACCOUNTCODE],[ACCOUNTINGARTICLECODE],[INVOICEID],[VALUEDATE],[ID_FACTURE],[QUANTITY],[AMOUNTWITHOUTTAX],[PRODUCTCODE],[STARTDATE],[LABEL],[TAXMODE],[AMOUNTWITHTAX],[ENDDATE],[AMOUNTTAX], [nx_hash], [nx_creation_date])
    SELECT [s].[UNITPRICE],[s].[UNITPRICECURRENCY],[s].[USERACCOUNTCODE],[s].[ACCOUNTINGARTICLECODE],[s].[INVOICEID],[s].[VALUEDATE],[s].[ID_FACTURE],[s].[QUANTITY],[s].[AMOUNTWITHOUTTAX],[s].[PRODUCTCODE],[s].[STARTDATE],[s].[LABEL],[s].[TAXMODE],[s].[AMOUNTWITHTAX],[s].[ENDDATE],[s].[AMOUNTTAX], hashbytes('MD5', (SELECT [s].[UNITPRICE],[s].[UNITPRICECURRENCY],[s].[USERACCOUNTCODE],[s].[ACCOUNTINGARTICLECODE],[s].[INVOICEID],[s].[VALUEDATE],[s].[ID_FACTURE],[s].[QUANTITY],[s].[AMOUNTWITHOUTTAX],[s].[PRODUCTCODE],[s].[STARTDATE],[s].[LABEL],[s].[TAXMODE],[s].[AMOUNTWITHTAX],[s].[ENDDATE],[s].[AMOUNTTAX] FOR XML RAW)), GETDATE()
    FROM
        [staging].[EXPORT_FACTURE_LIGNES_GAZ] as [s]
        left join [AG_to_OC].[EXPORT_FACTURE_LIGNES_GAZ] as [d] ON [s].[ID_FACTURE] = [d].[ID_FACTURE]
    WHERE
        [d].[ID_FACTURE] IS NULL;
